var fs = require('graceful-fs');

var noop = function () {};

function remove(path, callback) {
  fs.rm(path, { recursive: true, force: true }, callback || noop);
}

function removeSync (path) {
  fs.rmSync(path, { recursive: true, force: true });
}

module.exports = {
  remove: remove,
  removeSync: removeSync
};
